import { useParams, useNavigate } from 'react-router-dom';
import users from './../../static/data/Data.js'
import { useEffect } from 'react';
import './BusinessCardPage.css'
import axios from 'axios'
import fileDownload from 'js-file-download'
import http from 'http'

function App() {
  const navigate = useNavigate();
  let { id } = useParams();
  const currentUser = users.hasOwnProperty(id) ? users[id] : null;
  let backgroundImage = currentUser != null ? currentUser.image : ''
  let vcfFile = currentUser != null ? currentUser.file : ''
  useEffect(() => {
    if (currentUser == null) {
      navigate('/contacts/contactNotFound')
    }
  }, []);

  handleDownload = (url, filename) => {
    axios.get(url, {
      responseType: 'blob',
    })
    .then((res) => {
      fileDownload(res.data, filename)
    })
  }

  function handleClick() {
    const link = document.createElement('a');
    link.href = vcfFile;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  }

  return (
    <div className='container' style={{backgroundImage : `url(${backgroundImage})`}}>
      <div className='shadowBox'></div>
      <button className='download-button' onClick={() => handleDownload(vcfFile)}>Download Contact</button>
    </div>
  );
}

export default App;
